Rules.InvariantScript:
If(
    userrole = "Manager",
    Table(
        {
            menutext: "Dashboard",
            screen: 'Manager  dashboard',
            icon: Icon.Newspaper
        }, {
            menutext: "Leave Request",
            screen: 'Manager  dashboard',
            icon: Icon.CalendarBlank
        }, {
            menutext: "Permission Request",
            screen: 'Manager  dashboard',
            icon: Icon.Clock
        },{
            menutext: "All Employees",
            screen: 'All Employees',
            icon: Icon.People
        },{
            menutext: "Holidays List",
            screen: 'Holiday List screen',
            icon: Icon.AddToCalendar
        },{
            menutext: "News & Announcements",
            screen: 'Manager  dashboard',
            icon: Icon.Newspaper
        },{
            menutext: "settings",
            screen: 'Manager  dashboard',
            icon: Icon.Settings
        }
    ),
    If(
        userrole = "Employee",
        Table(
            {
                menutext: "Dashboard",
                screen: 'Employee dashboard',
                icon: Icon.Newspaper
            },
            {
            menutext: "Leave request",
            screen: 'Leave Screen',
            icon: Icon.CalendarBlank
        }, {
            menutext: "Permission Request",
            screen: 'Manager  dashboard',
            icon: Icon.Clock
        },{
            menutext: "Holidays List",
            screen: 'Holiday List screen',
            icon: Icon.AddToCalendar
        },{
            menutext: "News & Announcements",
            screen: 'Manager  dashboard',
            icon: Icon.Newspaper
        }, {
            menutext: "settings",
            screen: 'Manager  dashboard',
            icon: Icon.Settings
        }
        )
    )
)
